class bind:
    def changebind(self):
        if ui.pushButton1.text() == "解绑":
            ui.pushButton1.setText("绑定游戏")
            if ui.dm.UnBindWindow() == 1:
                ui.plainTextEdit.appendPlainText("解绑成功")
        else:
            ui.pushButton1.setText("解绑")
            window_id = FindWindow('TXGuiFoundation', '腾讯手游助手')
            window_id1 = FindWindowEx(window_id, None, None, None)
            window_id2 = FindWindowEx(window_id1, None, None, None)
            ui.dm.BindWindowEx(window_id2, "normal", "normal", "normal", "", 0)


class run:
    # 开始/停止按钮
    def changerun(self):
        if ui.pushButton.text() == "停止":
            ui.pushButton.setText("开始运行")
            sys.exit()
        else:
            ui.pushButton.setText("停止")
            if ui.checkBox.checkState() == 2:
                dailytask = src.mastertask.MasterTask()
                dailytask.task()




